'use client'
import { ark } from '@ark-ui/react/factory'
import { InfoIcon } from 'lucide-react'
import { type ComponentProps, forwardRef } from 'react'
import { createStyleContext } from 'styled-system/jsx'
import { alert } from 'styled-system/recipes'

const { withProvider, withContext } = createStyleContext(alert)

export type RootProps = ComponentProps<typeof Root>
export const Root = withProvider(ark.div, 'root')
export const Title = withContext(ark.h3, 'title')
export const Description = withContext(ark.div, 'description')
export const Content = withContext(ark.div, 'content')

type IndicatorProps = ComponentProps<typeof StyledIndicator>
const StyledIndicator = withContext(ark.span, 'indicator')

export const Indicator = forwardRef<HTMLSpanElement, IndicatorProps>(
  function Indicator(props, ref) {
    return (
      <StyledIndicator ref={ref} {...props}>
        <InfoIcon />
      </StyledIndicator>
    )
  },
)

import { defineSlotRecipe } from '@pandacss/dev'

export const alert = defineSlotRecipe({
  className: 'alert',
  slots: ['root', 'content', 'description', 'indicator', 'title'],
  base: {
    root: {
      alignItems: 'flex-start',
      borderRadius: 'l3',
      display: 'flex',
      position: 'relative',
      width: 'full',
    },
    content: {
      display: 'flex',
      flex: '1',
      flexDirection: 'column',
      gap: '1',
    },
    description: {
      display: 'inline',
    },
    indicator: {
      display: 'inline-flex',
      alignItems: 'center',
      justifyContent: 'center',
      flexShrink: '0',
    },
    title: {
      fontWeight: 'semibold',
    },
  },
  defaultVariants: {
    size: 'md',
    status: 'info',
    variant: 'subtle',
  },
  variants: {
    size: {
      md: {
        root: {
          gap: '3',
          p: '4',
          textStyle: 'sm',
        },
        indicator: {
          _icon: {
            width: '5',
            height: '5',
          },
        },
      },
      lg: {
        root: {
          gap: '4',
          p: '4',
          textStyle: 'md',
        },
        indicator: {
          _icon: {
            width: '6',
            height: '6',
          },
        },
      },
    },
    variant: {
      solid: {
        root: {
          layerStyle: 'solid',
        },
      },
      surface: {
        root: {
          layerStyle: 'surface',
        },
      },
      subtle: {
        root: {
          layerStyle: 'subtle',
        },
      },
      outline: {
        root: {
          layerStyle: 'outline',
        },
      },
    },
    status: {
      info: {
        root: { colorPalette: 'blue' },
      },
      warning: {
        root: { colorPalette: 'orange' },
      },
      success: {
        root: { colorPalette: 'green' },
      },
      error: {
        root: { colorPalette: 'red' },
      },
      neutral: {
        root: { colorPalette: 'gray' },
      },
    },
  },
})
