import { Slider as ArkSlider } from '@ark-ui/solid'
import { createStyleContext } from '~/lib/create-style-context'
import { styled } from '~/lib/styled'

const styles = tv({
  base: 'slider',
  defaultVariants: { size: 'md' },
  slots: {
    root: 'slider__root',
    label: 'slider__label',
    thumb: 'slider__thumb',
    valueText: 'slider__valueText',
    track: 'slider__track',
    range: 'slider__range',
    control: 'slider__control',
    markerGroup: 'slider__markerGroup',
    marker: 'slider__marker',
  },
  variants: {
    size: {
      sm: {
        root: 'slider__root--size_sm',
        label: 'slider__label--size_sm',
        thumb: 'slider__thumb--size_sm',
        valueText: 'slider__valueText--size_sm',
        track: 'slider__track--size_sm',
        range: 'slider__range--size_sm',
        control: 'slider__control--size_sm',
        markerGroup: 'slider__markerGroup--size_sm',
        marker: 'slider__marker--size_sm',
      },
      md: {
        root: 'slider__root--size_md',
        label: 'slider__label--size_md',
        thumb: 'slider__thumb--size_md',
        valueText: 'slider__valueText--size_md',
        track: 'slider__track--size_md',
        range: 'slider__range--size_md',
        control: 'slider__control--size_md',
        markerGroup: 'slider__markerGroup--size_md',
        marker: 'slider__marker--size_md',
      },
      lg: {
        root: 'slider__root--size_lg',
        label: 'slider__label--size_lg',
        thumb: 'slider__thumb--size_lg',
        valueText: 'slider__valueText--size_lg',
        track: 'slider__track--size_lg',
        range: 'slider__range--size_lg',
        control: 'slider__control--size_lg',
        markerGroup: 'slider__markerGroup--size_lg',
        marker: 'slider__marker--size_lg',
      },
    },
  },
})
const { withProvider, withContext } = createStyleContext(styles)

export const SliderRoot = withProvider(styled(ArkSlider.Root), 'root')
export const SliderControl = withContext(styled(ArkSlider.Control), 'control')
export const SliderLabel = withContext(styled(ArkSlider.Label), 'label')
export const SliderMarker = withContext(styled(ArkSlider.Marker), 'marker')
export const SliderMarkerGroup = withContext(styled(ArkSlider.MarkerGroup), 'markerGroup')
export const SliderRange = withContext(styled(ArkSlider.Range), 'range')
export const SliderThumb = withContext(styled(ArkSlider.Thumb), 'thumb')
export const SliderTrack = withContext(styled(ArkSlider.Track), 'track')
export const SliderValueText = withContext(styled(ArkSlider.ValueText), 'valueText')

export const Slider = Object.assign(SliderRoot, {
  Root: SliderRoot,
  Control: SliderControl,
  Label: SliderLabel,
  Marker: SliderMarker,
  MarkerGroup: SliderMarkerGroup,
  Range: SliderRange,
  Thumb: SliderThumb,
  Track: SliderTrack,
  ValueText: SliderValueText,
})
